    private void retornarTelaLogin(){
        TelaLogin telaLogin = new TelaLogin();
        this.setVisible(false);
        telaLogin.setVisible(true);
    }
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       retornarTelaLogin();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if(jTextField2.getText().equals(jTextField3.getText())){
            login.executeInsert(this, jTextField1.getText(), jTextField2.getText());
            setTextFields();
            retornarTelaLogin();
        }
        else{
            JOptionPaneDialogMessages.getInstance().errorMessage(this, "Senhas "
                    + "Divergentes");
        }
    }//GEN-LAST:event_jButton2ActionPerformed

